import React, { useRef } from "react";
import axios from "axios";
export default function Comp10() {
  var x1 = useRef();
  var x2 = useRef();
  var x3 = useRef();
  var x5 = useRef();
  function myFunc() {
    if (
      x1.current.value == "" ||
      x2.current.value == "" ||
      x3.current.value == ""
    ) {
      x2.current.innerHTML = "Please Provide All Fields...";
      x2.current.className = "alert alert-danger";
    } else {
      var record = {
        name: x1.current.value,
        price: x2.current.value,
        discount: x3.current.value,
      };
      console.log(record);
      axios
        .post("http://localhost:8080/add-tshirt", record)
        .then((result) => {
          console.log(result);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }
  return (
    <div>
      <h1>LogIn</h1>
      <input type="text" placeholder="Tshirt" id="" ref={x1} /> <br />
      <input type="number" placeholder="price" id="" ref={x2} /> <br />
      <input type="number" placeholder="discount" id="" ref={x3} /> <br />
      <button className="btn nth-dark" onClick={myFunc}>
        Add Tshirt Product
      </button>
      <p ref={x5}></p>
    </div>
  );
}
