import React, { useRef } from "react";
import axios from "axios";

export default function Product() {
  var x1 = useRef(); // capture  text box
  var x2 = useRef(); // error print
  function myFunc() {
    console.log(x1);
    console.log(x1.current.value);
    console.log(x2);
    if (x1.current.value == "") {
      x2.current.innerHTML = "Please enter Product...";
      x2.current.className = "alert alert-danger";
    } else {
      var record = { name: x1.current.value };
      console.log(record);
      axios.post("http://localhost:8080/add-product", record);
    }
  }

  return (
    <div className="container" ref={x1}>
      <h1>Add Product</h1>
      <select name="" id="">
        <option value="">Select Category</option>
      </select>
      <br />
      <select name="" id="">
        <option value="">Select Brand</option>
      </select>
      <br />
      <input type="text" placeholder="Name" id="" /> <br />
      <input type="text" placeholder="Price" id="" /> <br />
      <input type="file" />
      <br />
      <button className="btn nth-dark">Add</button>
      <p ref={x2}></p>
    </div>
  );
}
