import React, { useRef } from "react";
import { useSelector } from "react-redux";

export default function Checkout() {
  const answer = useSelector((state) => state.cart.cart);

  // Calculate the total price
    const totalPrice = answer?.reduce((acc, item) => acc + item.price, 0) || 0;
    var x1 = useRef();
    function myfunc() {
    var record = {}
}
    
  return (
    <div className="container">
      <h1>Checkout</h1>
      <table className="table">
        <thead>
          <tr>
            <th>Image</th>
            <th>Name</th>
            <th>Price</th>
            <th>Total Price</th>
          </tr>
        </thead>
        <tbody>
          {answer?.length > 0 ? (
            answer.map((value) => (
              <tr>
                <td>
                  <img
                    // http://localhost:8080/uploads/${value.path}
                    src={`http://localhost:8080/uploads/+{value.path}`}
                    alt="Product img"
                    className="img-fluid"
                    width="100px"
                  />
                </td>
                <td>{value.name}</td>
                <td>{value.price}</td>
                <td>{value.price}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4">Your cart is empty</td>
            </tr>
          )}
          {answer?.length > 0 && (
            <tr>
              <td colSpan="3">Total Price</td>
              <td>{totalPrice}</td>
            </tr>
          )}
        </tbody>
      </table>
      <h2>CheckOut Form</h2>
      <input type="text" placeholder="Firstname" className="form-control" />
      <br />
      <input type="text" placeholder="LastName" className="form-control" />
      <br />
      <input type="text" placeholder="email" className="form-control" /> <br />
      <input type="text" placeholder="phone no" className="form-control" />
      <br />
      <input type="text" placeholder="Amount" className="form-control" /> <br />
      <input type="text" placeholder="Product-info" className="form-control" />
      <br />
      <button onClick={myfunc}>PAY</button>
    </div>
  );
}
