import React, { useEffect, useRef, useState } from "react";
import axios from "axios";

export default function Product() {
  var x1 = useRef(); // capture  text box
  var x2 = useRef(); // error print
  var x3 = useRef();
  var x4 = useRef(); 
  var x11 = useRef();
  var x22= useRef(); 

  var [category, setCategory] = useState([]);
  var [brand, setbrand] = useState([]);
  var [file, setfile] = useState({}); // img store
  var [status, setStatus] = useState(false); // if img file not selected flase

  useEffect(() => {
    axios // this code showCategory
      .get("http://localhost:8080/show-categories")
      .then((response) => {
        console.log(response.data);
        setCategory(response.data);
      })
      .catch((error) => {
        console.error("Error fetching categories:", error);
      });
    axios //this code showBrands
      .get("http://localhost:8080/show-brands")
      .then((response) => {
        console.log(response.data);
        setbrand(response.data);
      })
      .catch((error) => {
        console.error("Error fetching brands:", error);
      });
  }, []);

  function myFunc(ev) {
    console.log(ev);
    console.log(ev.target.files[0]);
    setStatus(true);
    setfile(ev.target.files[0]);
  }
  return (
    <div className="container" ref={x1}>
      <h1>Add Product</h1>
      <select name="" id="" ref={11}>
        <option value="">Select Category</option>
        {category &&
          category.map((value) => (
            <option value={value._id}>{value.name}</option>
          ))}
      </select>
      <br />
      <select name="" id="" ref={22}>
        <option value="">Select Brand</option>
        {brand &&
          brand.map((val) => <option value={val._id}> {val.name}</option>)}
      </select>
      <br />
      <input type="text" placeholder="Name" id="" /> <br />
      <input type="text" placeholder="Price" id="" /> <br />
      <input type="file" onChange={myFunc} />
      <br />
      <button className="btn nth-dark">Add</button>
      <p ref={x2}></p>
    </div>
  );
}
